import React, {Component} from 'react';
import {connect} from 'react-redux';

import CarouselM from './carouselM';
import ActionMov from './actionMov';

import { fetchActionM } from '../../store/actions/moviepageActions';

class Movies extends Component{

    componentDidMount(){
        this.props.fetchActionM();
    }
    
    render(){
    
        console.log(this.props.action);
        return(
            <div className='container-fluid'>
                <div className='top-carousel-box'>
                {
                    this.props.action.map(({title, release_date, backdrop_path, id, overview, vote_average}) => 
                    <CarouselM title={title} key={id} release={release_date} backdrop={backdrop_path} overview={overview} vote={vote_average} />)
                }
                </div>
                    <h2 className='section-title'>Action movies trending now</h2>
                <div>
                    <ActionMov />
                </div>
                    <h2 className='section-title-con'>Plebs trending now</h2>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        action: state.movies.actions
    }
}


export default connect(mapStateToProps, { fetchActionM })(Movies);
