import React, { Component } from 'react';
import {connect} from 'react-redux';

import { fetchPersonInfo } from '../store/actions/homepageActions'

const img_url = 'https://image.tmdb.org/t/p/original';


class SearchPage extends Component {

    state = {
        personId: '',
        person_results: [],
    }

        componentDidMount = (props) => {
            //this.props.fetchPersonInfo(this.state.personId)
            let pid;
            if(this.props.results.length > 0){
                if (props.results[0].results[0].media_type === 'person'){
                    this.setState({person_results: props.results[0].results[0]});
                    pid = this.state.person_results.id;
                } else if (props.results[0].results[1].media_type === 'person'){
                    this.setState({person_results: props.results[0].results[1]})
                    pid = this.state.person_results.id;
                } else if (props.results[0].results[2].media_type === 'person'){
                    this.setState({person_results: props.results[0].results[2]})
                    pid = this.state.person_results.id;
                }
            }
            this.setState({
                personId: pid,
            })
        } 
        

    render(){
    let results;
        if(this.props.results.length > 0){
            console.log(this.props.results[0].results[0].media_type);
            results = this.props.results[0].results.map(rs => {
                if(rs.media_type === 'movie' && rs.poster_path !== null)
                    return (
                        <div className='.col-md-4 search-container' key={rs.id}>
                            <div className='search-results'>
                                <h5 className='search-title'>{rs.title}</h5>
                                <img className='search-image' src={`${img_url}${rs.poster_path}`} alt={rs.title} />
                                <p className='search-release'>{`Released: ${rs.release_date}`}</p>
                                <p className='search-vote'>{`Rating: ${rs.vote_average}`}</p>
                            </div>
                        </div>
                    )
                if (rs.poster_path === 'null' || rs.poster_path === '')
                        return (<div>{null}</div>)
                if (rs.media_type === 'tv' && rs.poster_path !== null)
                    return (
                    <div className='.col-md-4 search-container' key={rs.id}>
                        <div className='search-results'>
                            <h5 className='search-title'>{rs.name}</h5>
                            <img className='search-image' src={`${img_url}${rs.poster_path}`} alt={rs.title} />
                            <p className='search-release'>{`Aired: ${rs.first_air_date}`}</p>
                            <p className='search-vote'>{`Rating: ${rs.vote_average}`}</p>
                        </div>
                    </div>
                    )
                if (rs.poster_path === '' || rs.backdrop_path === '')
                        return (<div>{null}</div>);
            } 
            )
        }
            if (this.props.results.length > 0){
                if (this.props.results[0].results[0].media_type === 'person'){
                    console.log(this.props.results[0].results[0]);
                    console.log(this.state.person_results);
                    return (
                        <div className='.col-md-4 search-container'>
                                <div className='search-results-person'>
                                    <h5 className='search-title'>{this.state.person_results.name}</h5>
                                    <img className='search-image' src={`${img_url}${this.state.person_results.profile_path}`} alt={this.state.person_results.name} />
                                </div>
                        </div>
                    )
                } else if (this.props.results[0].results[1].media_type === 'person'){
                    console.log(this.state.person_results);
                    return (
                        <div className='.col-md-4 search-container'>
                                <div className='search-results-person'>
                                    <h5 className='search-title'>{this.state.person_results.name}</h5>
                                    <img className='search-image' src={`${img_url}${this.state.person_results.profile_path}`} alt={this.state.person_results.name} />
                                </div>
                        </div>
                    )
                } else if (this.props.results[0].results[2].media_type === 'person'){
                    console.log(this.state.person_results);
                    return (
                        <div className='.col-md-4 search-container'>
                                <div className='search-results-person'>
                                    <h5 className='search-title'>{this.state.person_results.name}</h5>
                                    <img className='search-image' src={`${img_url}${this.state.person_results.profile_path}`} alt={this.state.person_results.name} />
                                </div>
                        </div>
                    )
                } else {
                    return <div className='loading'>Loading...</div>
                }
            }

    /*let person;
        if (props.person.length > 0){
            person = props.person.map(person => {

            })
        }*/
            return (
                <div className='container-fluid'>
                        <div className='row'>
                                {results}
                                {this.state.personId}
                                {this.state.person_results}
                        </div>
                </div>
            )
        }
    }

const mapStateToProps = (state) => {
    return {
        results: state.movTv.results,
        person: state.movTv.personInfo
    }
}

export default connect(mapStateToProps, { fetchPersonInfo })(SearchPage);
