import React from 'react';
import {connect} from 'react-redux';

import { fetchPersonInfo } from '../store/actions/homepageActions'

const img_url = 'https://image.tmdb.org/t/p/original';

let personId;

const SearchPage = (props) => {
    let results;
        if(props.results.length > 0){
            console.log(props.results[0].results[0].media_type);
            results = props.results[0].results.map(rs => {
                if(rs.media_type === 'movie' && rs.poster_path !== null)
                    return (
                        <div className='.col-md-4' key={rs.id}>
                            <div className='search-results'>
                                <h5 className='search-title'>{rs.title}</h5>
                                <img className='search-image' src={`${img_url}${rs.poster_path}`} alt={rs.title} />
                                <p className='search-release'>{`Released: ${rs.release_date}`}</p>
                                <p className='search-vote'>{`Rating: ${rs.vote_average}`}</p>
                                {
                                //<p className='search-overview'>{rs.overview}</p>
                                }
                            </div>
                        </div>
                    )
                if (rs.poster_path === 'null' || rs.poster_path === '')
                        return (<div>{null}</div>)
                if (rs.media_type === 'tv' && rs.poster_path !== null)
                    return (
                    <div className='.col-md-4' key={rs.id}>
                        <div className='search-results'>
                            <h5 className='search-title'>{rs.name}</h5>
                            <img className='search-image' src={`${img_url}${rs.poster_path}`} alt={rs.title} />
                            <p className='search-release'>{`Aired: ${rs.first_air_date}`}</p>
                            <p className='search-vote'>{`Rating: ${rs.vote_average}`}</p>
                            {
                                //<p className='search-overview'>{rs.overview}</p>
                            }
                        </div>
                    </div>
                    )
                if (rs.poster_path === '' || rs.backdrop_path === '')
                        return (<div>{null}</div>);
            } 
            
            )
        }
        let person_results;
            if (props.results.length > 0){
                if (props.results[0].results[0].media_type === 'person'){
                    person_results = props.results[0].results[0];
                    personId = person_results.id;
                    props.fetchPersonInfo(personId);
                    return (
                        <div className='row'>
                        <div className='.col-md-4'>
                            <div className='search-results-person'>
                                <h5 className='search-title'>{person_results.name}</h5>
                                <img className='search-image' src={`${img_url}${person_results.profile_path}`} alt={person_results.name} />
                                <button onClick={() => props.fetchPersonInfo(personId)}>Click This</button>
                            </div>
                        </div>
                    </div>
                    )
                }
            }
                
 /*               person_results.map(pr => {
                    if (pr.media_type === 'person' && pr.profile_path !== null)
                        return (
                            <div className='row' key={pr.id}>
                            <div className='.col-md-4'>
                                <div className='search-results-person'>
                                    <h5 className='search-title'>{pr.name}</h5>
                                    <img className='search-image' src={`${img_url}${pr.profile_path}`} alt={pr.name} />
                                    <button onClick={() => props.fetchPersonInfo(personId)}>Click This</button>
                                </div>
                            </div>
                        </div>
                        ) */
/*
        if (rs.media_type === 'person');
        personId = rs.id;
        return (
                <div className='row' key={rs.id}>
                    <div className='.col-md-4'>
                        <div className='search-results-person'>
                            <h5 className='search-title'>{rs.name}</h5>
                            <img className='search-image' src={`${img_url}${rs.profile_path}`} alt={rs.name} />
                            <button onClick={() => props.fetchPersonInfo(personId)}>Click This</button>
                        </div>
                    </div>
                </div>
        )
*/
    /*let person;
        if (props.person.length > 0){
            person = props.person.map(person => {

            })
        }*/
    return (
            <div className='container-fluid'>
                <div className='row'>
                        {results}
                        {person_results}
                </div>
            </div>
    )
}

const mapStateToProps = (state) => {
    return {
        results: state.movTv.results,
        person: state.movTv.personInfo
    }
}

export default connect(mapStateToProps, { fetchPersonInfo })(SearchPage);
