import React, { Component } from 'react';
import { connect } from 'react-redux';

import { fetchPersonInfo, fetchPersonCredits } from '../store/actions/homepageActions';

const img_url = 'https://image.tmdb.org/t/p/original';

class SearchPage extends Component {
    
    state = {
        personId: '',
    }

    componentDidUpdate = () => {
        if(this.props.results.length > 0 && this.state.personId !== this.props.results[0].results[0].id){
            if(this.props.results[0].results[0].media_type === 'person'){
                this.setState({personId: this.props.results[0].results[0].id}, this.props.fetchPersonInfo(this.props.results[0].results[0].id));
            }
        } else if(this.props.results.length > 0 && this.state.personId !== this.props.results[0].results[1].id){
            if(this.props.results[0].results[1].media_type === 'person'){
                this.setState({personId: this.props.results[0].results[1].id}, this.props.fetchPersonInfo(this.props.results[0].results[1].id));
            }
        } else if(this.props.results.length > 0 && this.state.personId !== this.props.results[0].results[2].id){
            if(this.props.results[0].results[2].media_type === 'person'){
                this.setState({personId: this.props.results[0].results[2].id}, this.props.fetchPersonInfo(this.props.results[0].results[2].id));
            }
        } else {
            return (<div>{null}</div>)
        }
    }

    render(){
    //console.log(this.state.personId)
    let results;
        if(this.props.results.length > 0 && this.props.results[0].results[0].media_type !== 'person' && this.props.results[0].results[1].media_type !== 'person' && this.props.results[0].results[2].media_type !== 'person'){
            //console.log(this.props.results[0].results[0].media_type);
            results = this.props.results[0].results.map(rs => {
                if(rs.media_type === 'movie' && rs.poster_path !== null){
                    return (
                        <div className='.col-md-4 search-container' key={rs.id}>
                            <div className='search-results'>
                                <h5 className='search-title'>{rs.title}</h5>
                                <img className='search-image' src={`${img_url}${rs.poster_path}`} alt={rs.title} />
                                <p className='search-release'>{`Released: ${rs.release_date}`}</p>
                                <p className='search-vote'>{`Rating: ${rs.vote_average}`}</p>
                            </div>
                        </div>
                    )}
                if (rs.poster_path === 'null' || rs.poster_path === '' || rs.media_type === 'person'){
                        return (<div key={rs.id}>{null}</div>)}
                if (rs.media_type === 'tv' && rs.poster_path !== null){
                    return (
                    <div className='.col-md-4 search-container' key={rs.id}>
                        <div className='search-results'>
                            <h5 className='search-title'>{rs.name}</h5>
                            <img className='search-image' src={`${img_url}${rs.poster_path}`} alt={rs.title} />
                            <p className='search-release'>{`Aired: ${rs.first_air_date}`}</p>
                            <p className='search-vote'>{`Rating: ${rs.vote_average}`}</p>
                        </div>
                    </div>
                    )}
                if (rs.poster_path === '' || rs.backdrop_path === '' || rs.media_type === 'person'){
                        return (<div>{null}</div>);}
            } 
            )
        }
        let person_results;
            if (this.props.results.length > 0 ){
                if (this.props.results[0].results[0].media_type === 'person'){
                    //onsole.log(this.props.results[0]);
                    person_results = this.props.results[0].results[0];
                    return (
                        <div className='.col-md-4 search-container'>
                                <div className='search-results-person'>
                                    <h5 className='search-title'>{person_results.name}</h5>
                                    <img className='search-image' src={`${img_url}${person_results.profile_path}`} alt={person_results.name} />
                                </div>
                        </div>
                    )
                } else if (this.props.results[0].results[1].media_type === 'person'){
                    person_results = this.props.results[0].results[1];
                    return (
                        <div className='.col-md-4 search-container'>
                                <div className='search-results-person'>
                                    <h5 className='search-title'>{person_results.name}</h5>
                                    <img className='search-image' src={`${img_url}${person_results.profile_path}`} alt={person_results.name} />
                                </div>
                        </div>
                    )
                } else if (this.props.results[0].results[2].media_type === 'person'){
                    person_results = this.props.results[0].results[2];
                    return (
                        <div className='.col-md-4 search-container'>
                                <div className='search-results-person'>
                                    <h5 className='search-title'>{person_results.name}</h5>
                                    <img className='search-image' src={`${img_url}${person_results.profile_path}`} alt={person_results.name} />
                                </div>
                        </div>
                    )
                } else {
                    return <div className='loading'>Loading...</div>
                }
            }

    let person;
        if (this.props.person.length > 0){
            person = this.props.person.map(person => {
                return (
                    <div className='.col-md-8 search-container' key={person.id}>
                        <div className='person-results'>
                            <p className='person-bio'>{`${person.biography.substring(0, 500)}...`}</p>
                        </div>
                    </div>
                )
            })
        } else { return (<div>{null}</div>);}
            return (
                <div className='container-fluid'>
                        <div className='row'>
                                {results}
                                {person_results}
                                {person}
                        </div>
                </div>
            )
        }
    }

const mapStateToProps = (state) => {
    return {
        results: state.movTv.results,
        person: state.movTv.personInfo,
    }
}

export default connect(mapStateToProps, { fetchPersonInfo, fetchPersonCredits })(SearchPage);
