import React from 'react';
import {connect} from 'react-redux';
import Slider from 'react-slick';
import Modal from 'react-modal';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

import { fetchActionM } from '../../store/actions/moviepageActions';

const img_url = 'https://image.tmdb.org/t/p/original';

const customStyles = {
    content : {
      top                   : '50%',
      left                  : '50%',
      right                 : 'auto',
      bottom                : 'auto',
      marginRight           : '-50%',
      transform             : 'translate(-50%, -50%)',
      color                 : 'white',
      background: '#080a0a none repeat scroll 0% 0%',
      width: '600px',
    }
};

Modal.setAppElement('#root')

class ActionMov extends React.Component{
    constructor() {
        super();

        this.state = {
            modalIsOpen: false
        };

        this.openModal = this.openModal.bind(this);
        this.afterOpenModal = this.afterOpenModal.bind(this);
        this.closeModal = this.closeModal.bind(this);


    }

    openModal(movie) {
        this.setState({modalIsOpen: true});
    }

    afterOpenModal(){
        this.subtitle.style.color = '#f00';
    }

    closeModal(){
        this.setState({modalIsOpen: false});
    }

    render(){

    let movie = this.props.action;
    let action;
    if(this.props.action.length > 0){
        action = this.props.action[0].results.map(ac => (
            <div className='sliderbox' key={ac.id}>
                <div className='text-block'>
                    <h5 className='sliderTitle'>{ac.title}</h5>
                    <FontAwesomeIcon onClick={() => this.openModal(movie)} icon="plus-circle" className='sliderIcon' />
                    <p className='sliderRelease'>{ac.release_date}</p>
                    <p className='sliderVote'>{ac.vote_average}</p>
                </div>
                <img className='sliderImg' src={`${img_url}${ac.poster_path}`} alt={ac.title} />
            </div>
        ));
    }

    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 6,
        slidesToScroll: 3,
        draggable: true,
    };
        return (
            <div>
                <Slider {...settings}>
                    {action}
                </Slider>
                <Modal
                isOpen={this.state.modalIsOpen}
                onAfterOpen={this.afterOpenModal}
                onRequestClose={this.closeModal}
                style={customStyles}
                contentLabel='Movies modal'
                >
                <h2 ref={subtitle => this.subtitle = subtitle}>TITLE</h2>
                    <div>
                    <p>Id: </p>
                    <h5 className='modalRelease'>Released: </h5>
                    <h5 className='modalVote'>Rating: </h5>
                    </div>
                <button className='modalClose' onClick={this.closeModal}>X</button>
                </Modal>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        action: state.movies.actions
    }
}

export default connect(mapStateToProps, null)(ActionMov);
