import React, { Component } from 'react';
import {connect} from 'react-redux';

import Modal from 'react-modal';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

const customStyles = {
    content : {
      top                   : '50%',
      left                  : '50%',
      right                 : 'auto',
      bottom                : 'auto',
      marginRight           : '-50%',
      transform             : 'translate(-50%, -50%)',
      color                 : 'white',
      background            : '#080a0a none repeat scroll 0% 0%',
      width                 : '600px',
    }
};

Modal.setAppElement('#root')

class SearchCredits extends Component{
    state = {
        modalIsOpen: false,

    }

    openModal = () => {
        this.setState({ modalIsOpen: true });

    }

    afterOpenModal = () => {
        this.subtitle.style.color = '#f00';
    }

    closeModal = () => {
        this.setState({ modalIsOpen: false});
    }

    render(){

    let credits;
        if(this.props.credits.length > 0){
            credits = this.props.credits[0].cast.map(cr => {
                <div className='sliderbox' key={cr.id}>
                <div className='text-block'>
                    <h5 className='sliderTitle'>{cr.title}</h5>
                    <FontAwesomeIcon onClick={() => this.openModal(cr)} icon="plus-circle" className='sliderIcon' />
                    <p className='sliderRelease'>{cr.release_date}</p>
                    <p className='sliderVote'>{cr.vote_average}</p>
                </div>
                <img className='sliderImg' src={`${img_url}${cr.poster_path}`} alt={cr.title} />
            </div>
            })
        }

        return (
            <div>
                {credits}
            </div>
        )
    }

}

const mapStateToProps = (state) => {
    return {
        credits: state.movTv.credits
    }
}

export default connect(mapStateToProps, {})(SearchCredits);
