import React from 'react';
import {connect} from 'react-redux';
import Slider from 'react-slick';
import SliModal from './sliModal';


const img_url = 'https://image.tmdb.org/t/p/original';

const Trending = (props) => {
    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 6,
        slidesToScroll: 3,
        verticalSwiping: true,
        vertical: true,
        draggable: true,
    };

    let trending;
    if (props.trending.length > 0){
        //console.log(props.trending[0].results)
        trending = props.trending[0].results.map(tr => (
                <div className='sliderbox' key={tr.id}>
                    <div className='text-block'>
                        <h5 className='sliderTitle'>{tr.title}</h5>
                        <SliModal
                        toModal={tr}
                        img_url={img_url}
                        />
                        <p className='sliderRelease'>{tr.release_date}</p>
                        <p className='sliderVote'>{tr.vote_average}</p>
                    </div>
                    <img className='sliderImg' src={`${img_url}${tr.poster_path}`} alt={tr.title} />
                </div>
        ));
    }
    return (
        <Slider {...settings}>
            {trending}
        </Slider>
    )
}


const mapStateToProps = (state) => {
    return {
        trending: state.movTv.trending
    }
}

export default connect(mapStateToProps)(Trending);

