import { FETCH_SIMILAR, FETCH_TRENDING, FETCH_VIDEO, FETCH_TRENDING_TV, FETCH_VIDEO_TV, SEARCH_INPUT, FETCH_SEARCH_RESULT, FETCH_PERSON_INFO, FETCH_PERSON_CREDITS } from '../actions/actions';

const initState = {
    trending: [],
    videos: [],
    similar: [],
    trendingTv: [],
    videostv: [],
    searchInput: '',
    results: [],
    personInfo: [],
    credits: [],
}

const moviesTvReducer = (state = initState, action) => {
    switch (action.type){
        case FETCH_TRENDING:
        return {
            ...state,
            trending: [action.payload]
        }
        case FETCH_VIDEO:
        return {
            ...state,
            videos: [action.payload]
        }
        case FETCH_SIMILAR:
        return {
            ...state,
            similar: [action.payload]
        }
        case FETCH_TRENDING_TV:
        return {
            ...state,
            trendingTv: [action.payload]
        }
        case FETCH_VIDEO_TV:
        return {
            ...state,
            videostv: [action.payload]
        }
        case SEARCH_INPUT:
        return {
            ...state,
            searchInput: action.value
        }
        case FETCH_SEARCH_RESULT:
        return {
            ...state,
            results: [action.payload]
        }
        case FETCH_PERSON_INFO: 
        return {
            ...state,
            personInfo: [action.payload]
        }
        case FETCH_PERSON_CREDITS:
        return {
            ...state,
            credits: [action.payload]
        }
        default:
        return state;
    }
}


export default moviesTvReducer;
