import React from 'react';
import {Link} from 'react-router-dom';
import { connect } from 'react-redux';
import { fetchSearchResults, fetchPersonInfo } from '../store/actions/homepageActions';

const Navbar = (props) => {
    return (
        <span>
            <nav className='nav'>
                <Link to='/' className='nav-link'>Home</Link>
                <Link to='/movies' className='nav-link'>Movies</Link>
                <Link to='/tv-shows' className='nav-link'>Tv-Shows</Link>
                <span className='user-search-panel'>
                    <div className='form-group search'>
                    {
                        // function handleKeyPress is used here 
                    }
                        <input label='search' onChange={onInputChange} onKeyPress={(e) => handleKeyPress(e, props, tmpValue, personId)} className='form-control search' placeholder='Search...' />
                    </div>
                    <div className="dropdown">
                        <button className="btn btn-secondary dropdown-toggle" type="button" id="dropdownMenuButton" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                        Jack
                        </button>
                        <div className="dropdown-menu" aria-labelledby="dropdownMenuButton">
                            <a className="dropdown-item" href="http://google.fi">Action</a>
                            <a className="dropdown-item" href="http://google.fi">Another action</a>
                            <a className="dropdown-item" href="http://google.fi">Something else here</a>
                        </div>
                        </div>
                </span>
            </nav>
        </span>
    )
}

let tmpValue = null;
let personId = null;

const onInputChange = (event) => {
    tmpValue = event.target.value;
    console.log(tmpValue);
}

const handleKeyPress = (e, props, tmpValue) => {
    if(e.key === 'Enter'){        
        props.fetchSearchResults(tmpValue);
        personData();
    }
}

const personData = (props, personId) => {
    setTimeout(() => {if (props.data.length > 0){
        personId = props.data.results[0].id
        props.fetchPersonInfo(personId);
    } }, 1000 );
}

const mapStateToProps = (state) => {
    return {
        data: state.movTv.results
    }
}

export default connect(mapStateToProps, { fetchSearchResults, fetchPersonInfo })(Navbar);

