import React, { Component } from 'react';
import {connect} from 'react-redux';

import Modal from 'react-modal';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

const img_url = 'https://image.tmdb.org/t/p/original';

const customStyles = {
    content : {
      top                   : '50%',
      left                  : '50%',
      right                 : 'auto',
      bottom                : 'auto',
      marginRight           : '-50%',
      transform             : 'translate(-50%, -50%)',
      color                 : 'white',
      background            : '#080a0a none repeat scroll 0% 0%',
      width                 : '600px',
    }
};

Modal.setAppElement('#root')

class SearchCredits extends Component{
    state = {
        modalIsOpen: false,

    }

    openModal = () => {
        this.setState({ modalIsOpen: true });

    }

    afterOpenModal = () => {
        this.subtitle.style.color = '#f00';
    }

    closeModal = () => {
        this.setState({ modalIsOpen: false});
    }

    render(){

    let credits;
        if(this.props.credits.length > 0){
            console.log(this.props.credits[0].cast);
            credits = this.props.credits[0];
            credits.reduce((x, y) => x.findIndex(e => e.name == y.name) < 0 ? [...x, y] : x, []);
            credits.reduce((x, y) => x.findIndex(e => e.title == y.title) < 0 ? [...x, y] : x, []);
            credits.map(cr => {
            if(cr.poster_path !== null && cr.title !== null && cr.name !== null && cr.character !== '' && cr.character !== 'Himself' && ( cr.media_type=== 'tv' ? cr.name.length < 20 : cr.title.length < 20 ) && cr.vote_average > 5) {
                return (
                    <div className='-col-md-4 credit-box' key={cr.id}>
                        <div className='credit-block'>
                            <h5 className='search-title'>{`${cr.media_type === 'tv' ? cr.name : cr.title}`}</h5>
                            <FontAwesomeIcon onClick={() => this.openModal(cr)} icon="plus-circle" className='sliderIcon' />
                            <p className='search-release'>{`${cr.media_type === 'tv' ? cr.first_air_date : cr.release_date}`}</p>
                            <p className='search-vote'>{cr.vote_average}</p>
                        </div>
                        <img className='search-image' src={`${img_url}${cr.poster_path}`} alt={cr.title} />
                    </div>
               )}
        })
        }

        return (
            <div className='row'>
                {credits}
            </div>
        )
    }

}

const mapStateToProps = (state) => {
    return {
        credits: state.movTv.credits
    }
}

export default connect(mapStateToProps, {})(SearchCredits);
